package com.example.latihan_pam_3import FragmentActivityimport android.os.Bundleimport android.util.Logimport androidx.appcompat.app.AppCompatActivityimport com.example.latihan_pam_3.databinding.ActivityMainBindingclass MainActivity : AppCompatActivity() {    private lateinit var binding: ActivityMainBinding // Variabel binding    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        // Menginisialisasi binding        binding = ActivityMainBinding.inflate(layoutInflater)        val view = binding.root        setContentView(view)        // Mengakses elemen UI melalui variabel binding        binding.textView.text = "Pesan & Kesan Kuliah di ITERA"        binding.button.setOnClickListener {            Log.d("com.example.latihan_pam_3.MainActivity", "Button clicked")            // Inisialisasi EditText dan Button dari layout menggunakan View Binding            val editTextContent = binding.editText.text.toString()            val fragment = FragmentActivity().apply {                arguments = Bundle().apply {                    putString("mText", editTextContent)                }            }            // Menampilkan fragment di dalam frame layout            supportFragmentManager.beginTransaction()                .replace(binding.frameLayout.id, fragment)                .commit()        }        // Menambahkan hint ke EditText menggunakan View Binding        binding.editText.apply {            hint = "Masukkan Pesan"        }    }}